CREATE TABLE hash_list (id INT UNSIGNED AUTO_INCREMENT, tag VARCHAR(25), created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE hash_stats (id INT UNSIGNED AUTO_INCREMENT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE map_view (id INT UNSIGNED AUTO_INCREMENT, name VARCHAR(50) UNIQUE, pitch INT UNSIGNED, yaw INT UNSIGNED, roll INT UNSIGNED, latitude FLOAT(15, 6), longitude FLOAT(15, 6), created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, slug VARCHAR(255), UNIQUE INDEX map_view_sluggable_idx (slug), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE post (id INT UNSIGNED AUTO_INCREMENT, ip VARCHAR(16) NOT NULL, text VARCHAR(148), accuracy INT, latitude FLOAT(15, 6), longitude FLOAT(15, 6), created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
